"use strict";
const general = {
    createUL: function (arrayList) {
        let ulHTML = "<ul>";
        for (let j = 0; j < arrayList.length; j++) {
            ulHTML += '<li>'+arrayList[j]+'</li>';
        }
        return ulHTML + '</ul>';
    },
    createLines: function (arrayList) {
        let lineHTML = "";
        for (let j = 0; j < arrayList.length; j++) {
            lineHTML += arrayList[j] + '<br>';
        }
        return lineHTML;
    },
    compileExperience: function(arrayList) {
        let compiledExperience = "";
        for (let i = 0; i < arrayList.length; i++) {
            compiledExperience += `<div class="resume-item d-flex flex-column flex-md-row justify-content-between">
                <div class="resume-content">
                    <h3 class="mb-0">`+arrayList[i].position+`</h3>
                    <div class="subheading mb-3">`+arrayList[i].company+(arrayList[i].company==''?'':`: `)+arrayList[i].location+`</div>
                    <p>`+general.createUL(arrayList[i].description)+`</p>
                </div>
                <div class="resume-date text-md-right">
                    <span class="text-primary">`+arrayList[i].start+(arrayList[i].start==''?'':` - `)+arrayList[i].end+`</span>
                </div>
            </div>`;
        }
        return compiledExperience;
    },
    compileEducation: function(arrayList) {
        let compiledEducation = "";
        for (let i = 0; i < arrayList.length; i++) {
            compiledEducation += `<div class="resume-item d-flex flex-column flex-md-row justify-content-between mb-5">
              <div class="resume-content">
                <h3 class="mb-0">`+arrayList[i].school+`</h3>
                <div class="subheading mb-3">`+arrayList[i].degree+`</div>
                <div>`+general.createUL(arrayList[i].majors)+`</div>
                <p>`+general.createLines(arrayList[i].description)+`</p>
              </div>
              <div class="resume-date text-md-right">
                <span class="text-primary">`+arrayList[i].start+` - `+arrayList[i].end+`</span>
              </div>
            </div>`;
        }
        return compiledEducation;
    },
    compileSkills: function(arrayList, fabCheck) {
        let fabSkills = "";
        for (let i = 0; i < arrayList.length; i++) {
            if (fabCheck.includes(arrayList[i])) {
                fabSkills += `<li class="list-inline-item">
                    <i class="fab fa-`+arrayList[i]+`"></i>
                </li>`;
            } else {
                console.log('Skill not a fab icon: ' + arrayList[i]);
            }
        }
        return fabSkills;
    },
    compileWorkflow: function(arrayList) {
        let workflow = "";
        for (let i = 0; i < arrayList.length; i++) {
            workflow += `<li>
              <i class="fa-li fa fa-check"></i>
              `+arrayList[i]+`</li>`;
        }
        return workflow;
    },
    compileCertifications: function(arrayList) {
        let compiledCertifications = "";
        for (let i = 0; i < arrayList.length; i++) {
            compiledCertifications += `<li>
              <i class="fa-li fa fa-trophy text-warning"></i>
              `+arrayList[i]+`</li>`;
        }
        return compiledCertifications;
    }
}

