$(document).ready(function() {
"use strict"; // Start of use strict
    $("#objective_statement").text(resume.objective);
    $("#linkedin_link").attr('href', resume.linkedin);
    $("#github_link").attr('href', resume.github);
    $("#experience_list").append(general.compileExperience(resume.experience));
    $("#education_list").append(general.compileEducation(resume.education));
    $("#skills_list").append(general.compileSkills(resume.skills, fabCheck));
    $("#workflow_list").append(general.compileWorkflow(resume.workflows));
    $("#volunteer_list").append(general.compileExperience(resume.volunteer));
    $("#certifications_list").append(general.compileCertifications(resume.certifications));
    $("#hidden_address").on('dblclick', function() { $(this).html(resume.address); });
    $("#hidden_phone").on('dblclick', function() { $(this).html(resume.phone); });
    $("#hidden_email").on('dblclick', function() { $(this).html(resume.email); });
});
